import React, { useState, useEffect } from "react";

const ImageSlider = (props) => {
    const nftsList = props.list
    const showedNfts = props.showed
    const updateShowed = props.updateShowed

    var startIndex = 0
    var endIndex = startIndex + 4

    let next = (list) => {
        if (endIndex < list.length - 4) {
            startIndex += 4
            endIndex += 4
        } else {
            startIndex = endIndex
            endIndex = nftsList.length
        }
        updateShowed(nftsList.slice(startIndex, endIndex))
    }

    let prev = (list) => {
        if (startIndex > 4) {
            startIndex -= 4
            endIndex -= 4
        } else {
            startIndex = 0
            endIndex = 4
        }
        updateShowed(nftsList.slice(startIndex, endIndex))
    }

    return (
        <div className="similar-wrapper">
            <a className="similar-prev" onClick={() => { prev(nftsList) }}>&#10094;</a>
            <div className="similar-image-container">
                {showedNfts.map((item, index) => (
                    <div key={index} className="similar-image">{item.base64Img}</div>
                ))}
            </div>
            <a className="similar-next" onClick={() => { next(nftsList) }}>&#10095;</a>
        </div>)
}

export default ImageSlider
