import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { useParams, Link } from "react-router-dom";
const url = "http://127.0.0.1:5000/nftDetails?id=";

const SingleNft = () => {
  const { Id } = useParams();

  const [singleNft, setSingleNft] = useState(null);
  const getSingleNft = async () => {
    const response = await fetch(url + Id);
    const data = await response.json();
    console.log(data.drinks[0]);
    setSingleNft(data.drinks[0]);
  };
  useEffect(() => {
    getSingleNft();
  }, []);
  if (!singleNft) {
    return <Loading />;
  }
  const { similarityScore, base64Img, category, coco_url, collection, date_captured, file_name,
    file_path, flickr_url, generated_caption, height, id, license, 
    original_caption, similarity, width } = singleNft

  return (
    <>
      {singleNft && (
        <section className="section nftDetails-section">
          <Link to={"/"} className="btn btn-primary">
            back home
          </Link>
          <h2></h2>
          <div className="nftDetails">
            <img src={strDrinkThumb} alt={strDrink} />
            <div className="nft-info">
              <p>
                <span className="nftDetails-data">name :</span>
                {strDrink}
              </p>
              <p>
                <span className="nftDetails-data">Category :</span>
                {strCategory}
              </p>
              <p>
                <span className="nftDetails-data">Info :</span>
                {strAlcoholic}
              </p>
              <p>
                <span className="nftDetails-data">Glass :</span>
                {strGlass}
              </p>
              <p>
                <span className="nftDetails-data">Instuctions :</span>
                {strInstructions}
              </p>
              <p>
                <span className="nftDetails-data">Ingredients :</span>
                <span> {strIngredient1}</span>
                <span> {strIngredient2}</span>
                <span> {strIngredient3}</span>
              </p>
            </div>
          </div>
        </section>
      )}
    </>
  );
};

export default SingleNft;
