import React from 'react'
import { Link } from 'react-router-dom'

const NftRow = ({ nft }) => {
    const { similarityScore, base64Img, category, coco_url, collection, date_captured, file_name,
        file_path, flickr_url, generated_caption, height, id, license,
        original_caption, similarity, width } = nft

    const similarityScoreView = similarityScore == -1 ? 
    <p></p> : <p>Similarity Score = {similarityScore}</p>
    const generated_captionView = String(generated_caption).length > 40 ? 
    <i>{String(generated_caption).substring(0, 39)} ...</i> : <i>{generated_caption}</i>

    return (
        <div className="nft-row">
            <img className='img-row' src={`data:image/jpeg;base64,${base64Img}`} />
            <b style={{marginLeft:"100px", marginRight:"10px"}}>Collection: </b>{collection} 
            <b style={{marginLeft:"100px", marginRight:"10px"}}>Description: </b>
            {generated_captionView} {similarityScoreView}
            <Link className='btn-row' to={'/nft/' + id}  style={{display:"inline-block"}} >Details</Link>
        </div>

    )
}
export default NftRow
