import React from 'react'
import { useGlobalContext } from '../context'
import ReactSlider from "react-slider";


const SearchForm = () => {
  const { nftInput, setNftInput, searchPressed, setSearchPressed } = useGlobalContext()

  setSearchPressed(false)

  const handleChange = (e) => {
    e.preventDefault()
    setNftInput(e.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    setSearchPressed(true)
  }

  return (
    <div>
      <section className="section search">
        <form className="search-form" onSubmit={handleSubmit}>
          <div className="form-control">
            <label htmlFor="name">Insert your nft description</label>
            <input type="text" id="name" name="name" value={nftInput} onChange={handleChange} />
          </div>
        </form>
      </section >

      <ReactSlider
        className="horizontal-slider"
        thumbClassName="example-thumb"
        trackClassName="example-track"
        renderThumb={(props, state) => <div {...props}>{state.valueNow}</div>}
      />
    </div>
  )
}

export default SearchForm
