import React from "react";
import Nft from "./Nft";
import Loading from "./Loading";
import { useGlobalContext } from "../context";
import { useState, useEffect } from "react";

const NftList = () => {
  const { nfts, isLoading } = useGlobalContext();
  const [detailView, setDetailView] = useState(false)
  var toShow

  const renderCardView = (nfts) => {
    return (
      <div className="nfts-center">
        {nfts.map((nft, index) => {
          return <Nft key={index} nft={nft} />;
        })}
      </div>)
  }

  const renderListView = (nfts) => {
    return (
      <div className="nfts-center">
        {nfts.map((nft, index) => {
          return <Nft key={index} nft={nft} />;
        })}
      </div>)
  }

  useEffect(() => {
    if (detailView) {
      toShow = renderCardView
    } else {
      toShow = renderListView
    }
  }, [detailView])


  if (isLoading === true) {
    return <Loading />;
  }
  if (nfts === null || nfts.length == 0) {
    return (
      <h2 className="section-title">no nfts matched your search criteria</h2>
    )
  }
  return (
    <section className="section">
      <h2 className="section-title">nfts</h2>
      <div className="btn-block-switchView" style={{ display: "inline-block", alignContent: "center" }}>
        <button className="btn btn-primary-switchView" 
        onClick={()=>{setDetailView(true); window.location.reload}}>
          Card View</button>
        <button className="btn btn-primary-switchView" onClick={()=>{setDetailView(false)}}>
          List View</button>
      </div>
      {renderCardView(nfts)}
    </section>
  );
};

export default NftList;
