import React from 'react'
import { useGlobalContext } from '../context'

const SearchForm = () => {
  const { nftInput, setNftInput, searchPressed, setSearchPressed } = useGlobalContext()

  setSearchPressed(false)

  const handleChange = (e) => {
    e.preventDefault()
    setNftInput(e.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    setSearchPressed(true)
  }

  const value = 50

  return (
      <section className="section search">
        <form className="search-form" onSubmit={handleSubmit}>
          <div className="form-control">
            <label htmlFor="name">Insert your nft description</label>
            <input type="text" id="name" name="name" value={nftInput} onChange={handleChange} />
          </div>
        </form>
      </section>

  )
}

export default SearchForm
