import React from 'react'
import { Link } from 'react-router-dom'

const NftRow = ({ nft }) => {
    const { similarityScore, base64Img, category, coco_url, collection, date_captured, file_name,
        file_path, flickr_url, generated_caption, height, id, license,
        original_caption, similarity, width } = nft

    const similarityScoreView = similarityScore == -1 ? <p></p> : <p>Similarity Score = {similarityScore}</p>
    const originalCaptionView = original_caption.length > 250 ? <p>{original_caption.substring(0, 249)} ...</p> : <p>{original_caption}</p>

    return (
        <div className="nft-row">
            <img className='img-row' src={`data:image/jpeg;base64,${base64Img}`} />
            <b>Collection: </b>{collection} <b>Description:   </b>{generated_caption} {similarityScoreView}
            <Link className='btn-row' to={'/nft/' + id}>Details</Link>
        </div>

    )
}
export default NftRow
